action.body:
var parts = type.split('_');
var documentName = (parts[2] == 'rootfolder') ? parts[2] : parts[1];
return this.getDocumentEditorByDocumentName(documentName);

var items = listWidget.getSelectedItems();
if (items.length == 1)
{
	var item = items[0];
	var panel = (item.id == this.getRootFolderId()) ? null : 'properties';
	this.openDocumentEditor(items[0], panel);
}
